mapper.select:
select

from tb_md m left join tb_md_category mc on m.md_category_id=mc.md_category_id
where m.md_id=#{mdId}

select
from tb_md m left join tb_md_category mc on m.md_category_id=mc.md_category_id
limit #{index},#{pageSize}

select count(0) from ( select
from tb_md m left join tb_md_category mc on m.md_category_id=mc.md_category_id
) c
